using System;
using Newtonsoft.Json;

namespace TdLib
{
    /// <summary>
    /// Autogenerated TDLib APIs
    /// </summary>
    public static partial class TdApi
    {
        /// <summary>
        /// Position on a photo where a mask should be placed 
        /// </summary>
        public class MaskPosition : Object
        {
            /// <summary>
            /// Data type for serialization
            /// </summary>
            [JsonProperty("@type")]
            public override string DataType { get; set; } = "maskPosition";

            /// <summary>
            /// Extra data attached to the message
            /// </summary>
            [JsonProperty("@extra")]
            public override string Extra { get; set; }

            /// <summary>
            /// Part of the face, relative to which the mask should be placed
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("point")]
            public MaskPoint Point { get; set; }

            /// <summary>
            /// Shift by X-axis measured in widths of the mask scaled to the face size, from left to right. (For example, -1.0 will place the mask just to the left of the default mask position)
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("x_shift")]
            public double? XShift { get; set; }

            /// <summary>
            /// Shift by Y-axis measured in heights of the mask scaled to the face size, from top to bottom. (For example, 1.0 will place the mask just below the default mask position)
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("y_shift")]
            public double? YShift { get; set; }

            /// <summary>
            /// Mask scaling coefficient. (For example, 2.0 means a doubled size)
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("scale")]
            public double? Scale { get; set; }
        }
    }
